function [isFar]=checkDistance(dist, centerDist, mylist, new)%[isFar]=checkDistance(dist, centerDist, mylist, new)%checks to make sure the distance between a new  point and all %the points in mylist is greater than dist and greater %than centerDist from myCentertheCenter = mylist(1,:);theDistance = getDistance(theCenter, new);if theDistance < centerDist	isFar = 0;	returnendN = 2;while N <= size(mylist,1)	nextPoint = mylist(N,:);	theDistance = getDistance(nextPoint, new);	if theDistance < dist		isFar = 0;		return	end	N = N + 1;endisFar = 1;